		title = TMW.L["SORTBYNONE"],
		tooltip = TMW.L["SORTBYNONE_META_DESC"],
	},
	{
		setting = "Sort",
		value = -1,
		title = TMW.L["ICONMENU_SORTASC"],
		tooltip = TMW.L["ICONMENU_SORTASC_META_DESC"],
	},
	{
		setting = "Sort",
		value = 1,
		title = TMW.L["ICONMENU_SORTDESC"],
		tooltip = TMW.L["ICONMENU_SORTDESC_META_DESC"],
	},
})
